'use strict';;let Model;_982‍.w("sequelize",[["Model",["Model"],function(v){Model=v}]]);


module.exports = (sequelize, DataTypes) => {
  class Product extends Model {
    static associate(models) {
      const {User, Color} = models;
      Product.belongsTo(User, {foreignKey: 'user_id'})
      Product.belongsToMany(Color, { foreignKey: 'product_id', through: { model:'ProductColors', attributes: []}, as: 'colors' })
    }
  }

  Product.init({
    productName: DataTypes.STRING,
    user_id: {
      type: DataTypes.INTEGER,
      references: {
        model: 'Users',
        key: 'id'
      },
      onDelete: "cascade",
    },

  }, {
    sequelize,
    modelName: 'Product',
  });
  return Product;
};